package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting band. * autogenerated from schema use as a
 * shell which can be edited
 *
 */
public class CMLBand extends AbstractBand {

    /**
     * constructor.
     */
    public CMLBand() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLBand(CMLBand old) {
        super((AbstractBand) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLBand(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLBand
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLBand();

    }
}
