package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting module. * autogenerated from schema use as a
 * shell which can be edited
 *
 */
public class CMLModule extends AbstractModule {

	/** namespaced element name.*/
	public final static String NS = C_E+TAG;

    /**
     * constructor.
     */
    public CMLModule() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLModule(CMLModule old) {
        super((AbstractModule) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLModule(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLModule
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLModule();

    }
}
