package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting observation. * autogenerated from schema use
 * as a shell which can be edited
 *
 */
public class CMLObservation extends AbstractObservation {

    /**
     * constructor.
     */
    public CMLObservation() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLObservation(CMLObservation old) {
        super((AbstractObservation) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLObservation(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLObservation
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLObservation();

    }
}
