package org.xmlcml.cml.element.main;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting mechanism. * autogenerated from schema use
 * as a shell which can be edited
 *
 */
public class CMLMechanism extends AbstractMechanism {

	/** namespaced element name.*/
	public final static String NS = C_E+TAG;

    /**
     * constructor.
     */
    public CMLMechanism() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLMechanism(CMLMechanism old) {
        super((AbstractMechanism) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLMechanism(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLMechanism
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLMechanism();

    }
}
